    VALUES (#{name}, #{location}, #{type}, #{id}, #{info}, #{bookingScheduleStart}, #{bookingScheduleEnd}, #{resourceState});
INSERT INTO LIBRO (id, author, hard_cover, pages, genre)
    VALUES (#{id}, #{author}, #{hardCover}, #{pages}, #{genre});

